package com.example.propert_app.model;import com.google.gson.annotations.Expose;import com.google.gson.annotations.SerializedName;import java.util.List;public class HomeModel {    @SerializedName("status")    @Expose    public Integer status;    @SerializedName("message")    @Expose    public String message;    @SerializedName("data")    @Expose    public Data data;    public HomeModel(Integer status, String message, Data data) {        super();        this.status = status;        this.message = message;        this.data = data;    }public class Category {    @SerializedName("id")    @Expose    public Integer id;    @SerializedName("name")    @Expose    public String name;    @SerializedName("type")    @Expose    public String type;    @SerializedName("icon")    @Expose    public String icon;    /**     * No args constructor for use in serialization     *     */    public Category() {    }    /**     *     * @param name     * @param icon     * @param id     * @param type     */    public Category(Integer id, String name, String type, String icon) {        super();        this.id = id;        this.name = name;        this.type = type;        this.icon = icon;    }}public class Data {    @SerializedName("properties")    @Expose    public List<Property> properties = null;    @SerializedName("categories")    @Expose    public List<Category> categories = null;    /**     * No args constructor for use in serialization     *     */    public Data() {    }    /**     *     * @param categories     * @param properties     */    public Data(List<Property> properties, List<Category> categories) {        super();        this.properties = properties;        this.categories = categories;    }}public class Property {    @SerializedName("id")    @Expose    public Integer id;    @SerializedName("attachments")    @Expose    public String attachments;    @SerializedName("name")    @Expose    public String name;    @SerializedName("location")    @Expose    public String location;    @SerializedName("minprice")    @Expose    public String minprice;    @SerializedName("maxprice")    @Expose    public String maxprice;    /**     * No args constructor for use in serialization     *     */    public Property() {    }    public Property(Integer id, String attachments, String name, String location, String minprice, String maxprice) {        super();        this.id = id;        this.attachments = attachments;        this.name = name;        this.location = location;        this.minprice = minprice;        this.maxprice = maxprice;    }}}